import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [bugs, setBugs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/bugs')
      .then((res) => {
        setBugs(res.data);
        setLoading(false);
      })
      .catch((err) => {
        setError('Failed to fetch bugs. Please try again later.');
        setLoading(false);
      });
  }, []);

  return (
    <div className="dashboard">
      <h1>Bug Tracker Dashboard</h1>

      {loading && <p>Loading bugs...</p>}
      {error && <p className="error">{error}</p>}

      {!loading && !error && (
        <table className="bug-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {bugs.map((bug) => (
              <tr key={bug._id} className={`status-${bug.status.toLowerCase()}`}>
                <td>{bug._id}</td>
                <td>{bug.title}</td>
                <td>{bug.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Dashboard;
